int jumlahkan(int a, int b) {
    return a + b;
}

void cetak(string nama); // Prototype (Janji)

int main() {
    cetak("Budi"); // Aman, udah kenalan
    return 0;
}

void cetak(string nama) { // Definisi Asli
    cout << nama;
}

int angka[3]; // Deklarasi doang (Isinya sampah memori)
int nilai[] = {10, 20, 30}; // Auto size 3

// Looping Array
for(int i=0; i<3; i++) {
    cout << nilai[i] << endl;
}

int matriks[2][3] = {
    {1, 2, 3}, // Baris 0
    {4, 5, 6}  // Baris 1
};

// Akses angka 6 (Baris 1, Kolom 2)
cout << matriks[1][2];

// WAJIB bawa parameter ukuran (size)
void cetakArray(int arr[], int size) {
    for(int i=0; i<size; i++) cout << arr[i];
}

int main() {
    int data[] = {1,2,3};
    cetakArray(data, 3);
}

void kaliDua(int arr[], int n) {
    for(int i=0; i<n; i++) {
        arr[i] *= 2; // Mengubah data di Main!
    }
}

// Fungsi hitung total (Return value)
int total(const int arr[], int n) {
    int sum = 0;
    for(int i=0; i<n; i++) sum += arr[i];
    return sum;
}
