#include <iostream>
using namespace std;

int main() {
    cout << "Pola Loncat Indah:\n";
    for (int i = 1; i <= 50; i++) {
        // Filter Filter Filter
        if (i >= 11 && i <= 20) continue;
        if (i >= 31 && i <= 40) continue;
        if (i > 41) continue;

        cout << i << " ";
    }
    return 0;
}

