void bubbleSortAsc(int arr[], int n) {
    // Loop i: Menghitung berapa kali kita harus mengulang proses
    for(int i = 0; i < n-1; i++) {

        // Loop j: Jalan dari depan, bandingin tetangga
        // Kenapa 'n-i-1'? Karena i angka di belakang udah pasti urut!
        for(int j = 0; j < n-i-1; j++) {

            // Kalau KIRI LEBIH GEDE dari KANAN, TUKAR!
            if(arr[j] > arr[j+1]) {
                int temp = arr[j];
                arr[j] = arr[j+1];
                arr[j+1] = temp;
            }
        }
    }
}

void bubbleSortBackwards(int arr[], int n) {
    for(int i = 0; i < n-1; i++) {

        // Loop j MULAI DARI BELAKANG (n-1)
        // Berhenti di 'i' (karena posisi 0..i udah diisi angka kecil)
        // Pake 'j--' buat mundur
        for(int j = n-1; j > i; j--) {

            // Bandingin sama tetangga SEBELAH KIRI (j-1)
            // Kalau KANAN LEBIH KECIL dari KIRI, Tukar!
            if(arr[j] < arr[j-1]) {
                int temp = arr[j];
                arr[j] = arr[j-1];
                arr[j-1] = temp;
            }
        }
    }
}

void bubbleSortOptimized(int arr[], int n) {
    for(int i = 0; i < n-1; i++) {
        bool ditukar = false; // Reset flag tiap ronde

        for(int j = 0; j < n-i-1; j++) {
            if(arr[j] > arr[j+1]) {
                // Ada pertukaran, tandai!
                swap(arr[j], arr[j+1]); 
                ditukar = true;
            }
        }

        // Cek CCTV: Kalau gak ada yang ditukar, STOP (Break)
        if (ditukar == false) {
            break; 
        }
    }
}

#include <iostream>
using namespace std;

void bubbleBackwards(int arr[], int n) {
    cout << "Proses Sorting Mundur:" << endl;
    for(int i = 0; i < n-1; i++) {
        cout << "Ronde " << i+1 << ": ";
        for(int j = n-1; j > i; j--) {
            if(arr[j] < arr[j-1]) {
                swap(arr[j], arr[j-1]);
            }
        }
        // Intip array tiap ronde
        for(int k=0; k<n; k++) cout << arr[k] << " ";
        cout << endl;
    }
}

int main() {
    int data[] = {64, 22, 11, 90, 12};
    int n = 5;

    bubbleBackwards(data, n);

    cout << "\nHasil Akhir: ";
    for(int i=0; i<n; i++) cout << data[i] << " ";

    return 0;
}

