#include <iostream>
#include <algorithm> // Buat fungsi swap bawaan
using namespace std;

void selectionSort(int arr[], int n) {
    for(int i = 0; i < n-1; i++) {
        int minIdx = i;
        for(int j = i+1; j < n; j++) {
            if(arr[j] < arr[minIdx]) minIdx = j;
        }
        swap(arr[i], arr[minIdx]);

        // Debugging: Liat prosesnya
        cout << "Step " << i+1 << ": ";
        for(int k=0; k<n; k++) cout << arr[k] << " ";
        cout << endl;
    }
}

int main() {
    int nilai[] = {65, 20, 90, 45, 10};
    int n = 5;
    cout << "Awal: 65 20 90 45 10" << endl;
    selectionSort(nilai, n);
    return 0;
}
